import React from "react";
import axios from "axios";

class UserSignupPage extends React.Component {

    state = {
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null,
        pendingApiCall:false
    }

    onChange = event => {
        const value = event.target.value;
        const name = event.target.name;

        this.setState({
            [name]: value
        })


    }


    onClickSignUp = event => {

        event.preventDefault()

        const {username, displayName, password} = this.state
        const body = {
            username,
            displayName,
            password
        }

        this.setState({
            pendingApiCall:true
        })


        axios.post('/api/1.0/users', body).then((response => {
            this.setState({pendingApiCall:false})
        }))

    }

    render() {
        return (
            <div className="container">
                <form>

                    <div className="text-center">
                        <h1>SignUp</h1>
                    </div><br/>

                    <div className="form-group">
                        <label>Username</label>
                        <input className="form-control" type="text" name="username" onChange={this.onChange}/>
                    </div>
                    <br/>

                    <div className="form-group">
                        <label>DisplayName</label>
                        <input className="form-control" type="text" name="displayName" onChange={this.onChange}/>
                    </div>
                    <br/>

                    <div className="form-group">
                        <label>Password</label>
                        <input className="form-control" type="password" name="password" onChange={this.onChange}/>
                    </div>
                    <br/>

                    <div className="form-group">
                        <label>PasswordRepeat</label>
                        <input className="form-control" type="password" name="passwordRepeat" onChange={this.onChange}/>
                    </div>
                    <br/>

                    <div className="text-center">
                        <button className="btn btn-success text-center" onClick={this.onClickSignUp} disabled={this.state.pendingApiCall}>Sign Up</button>
                    </div>
                </form>

            </div>

        )
    }


}

export default UserSignupPage
